import React from "react";
import ReactDOM from "react-dom";
//import "./styles.css";
import { ResponsiveGeoMap } from "@nivo/geo";
import { ResponsiveGeoMapCanvas } from "@nivo/geo";
import { ResponsiveChoropleth } from "@nivo/geo";
import countries from "./world_countries.json";
import data from "./NivoMapContainer";

function NivoMap() {
  return (
    <div className="App">
      <h1>@nivo/geo</h1>
      <div style={{ height: "400px" }}>
        <MyResponsiveChoropleth data={data} />
      </div>
      <hr />
      <div style={{ height: "400px" }}>
        <MyResponsiveGeoMap />
      </div>
      <hr />
      <div style={{ height: "400px" }}>
        <MyResponsiveGeoMapCanvas />
      </div>
    </div>
  );
}

const MyResponsiveGeoMap = () => (
  <ResponsiveGeoMap
    features={countries.features}
    projectionTranslation={[0.5, 0.5]}
    fillColor="#eeeeee"
    borderWidth={0.5}
    borderColor="#333333"
    enableGraticule={true}
    graticuleLineColor="#666666"
  />
);

const MyResponsiveGeoMapCanvas = () => (
  <ResponsiveGeoMapCanvas
    features={countries.features}
    projectionTranslation={[0.5, 0.5]}
    fillColor="#eeeeee"
    borderWidth={0.5}
    borderColor="#333333"
    enableGraticule={true}
    graticuleLineColor="#666666"
  />
);

const MyResponsiveChoropleth = ({ data }) => (
  <ResponsiveChoropleth
    data={data}
    features={countries.features}
    colors="nivo"
    unknownColor="#666666"
    label="properties.name"
    valueFormat=".2s"
    projectionTranslation={[0.5, 0.5]}
    enableGraticule={true}
    graticuleLineColor="#dddddd"
    borderWidth={0.5}
    borderColor="#152538"
    legends={[
      {
        anchor: "bottom-left",
        direction: "column",
        justify: true,
        translateX: 20,
        translateY: -100,
        itemsSpacing: 0,
        itemWidth: 94,
        itemHeight: 18,
        itemDirection: "left-to-right",
        itemTextColor: "#444444",
        itemOpacity: 0.85,
        symbolSize: 18,
        effects: [
          {
            on: "hover",
            style: {
              itemTextColor: "#000000",
              itemOpacity: 1,
            },
          },
        ],
      },
    ]}
  />
);

export default NivoMap;
