import React, { useState, useEffect } from "react";
import MainStatsCard from '../MainStatsCard/MainStatsCard';
import { Grid, Row, Col } from "react-bootstrap";
import { sortData, prettyPrintStat } from "./util";
import numeral from "numeral";


const HeaderStats = () => {
    const [countryInfo, setCountryInfo] = useState({});

    useEffect(() => {
        fetch("https://disease.sh/v3/covid-19/all")
          .then((response) => response.json())
          .then((data) => {
            setCountryInfo(data);
          });
    }, []);

    const mildCases =  parseInt(countryInfo.active)-parseInt(countryInfo.critical);
    const casesWithOutcome = parseInt(countryInfo.cases)-parseInt(countryInfo.recovered);

    return(
        <Row>
            <Col lg={3} sm={12}>
              <MainStatsCard
                title= 'TOTAL ACTIVE CASES'
                cases1 = {countryInfo.active}
                subheading1 = 'Total infected patients'
                cases2 = {countryInfo.critical}
                subheading2 = 'in Mild condition'
                cases3 = {mildCases}
                subheading3 = 'Serious/Critical'
              />
            </Col>
            <Col lg={3} sm={12}>
            <MainStatsCard
                title= 'TOTAL CLOSED CASES'
                cases1 = {casesWithOutcome}
                subheading1 = 'Cases with an outcome'
                cases2 = {countryInfo.recovered}
                subheading2 = 'Recovered'
                cases3 = {countryInfo.deaths}
                subheading3 = 'Deaths'
              />
            </Col>
            <Col lg={3} sm={12}>
            <MainStatsCard
                title= 'TODAY ACTIVE CASES'
                cases1 = {countryInfo.todayCases}
                subheading1 = 'Active cases'
                cases2 = {countryInfo.todayRecovered}
                subheading2 = 'Recovered cases'
                cases3 = {countryInfo.todayDeaths}
                subheading3 = 'Deaths'

                cases={prettyPrintStat(countryInfo.todayCases)}
            total={numeral(countryInfo.cases).format("0.0a")}
              />
            </Col>
            <Col lg={3} sm={12}>
            <MainStatsCard
                title= 'CASES PER ONE MILLION'
                cases1 = {countryInfo.casesPerOneMillion}
                subheading1 = 'Cases/1 million'
                cases2 = {countryInfo.recoveredPerOneMillion}
                subheading2 = 'Recovered/1 million'
                cases3 = {countryInfo.deathsPerOneMillion}
                subheading3 = 'Deaths/1 million'
              />
            </Col>
          </Row>
    );


};

export default HeaderStats
