import React, { useState, useEffect } from "react";
import MainStatsCard from '../MainStatsCard/MainStatsCard';
import { Grid, Row, Col } from "react-bootstrap";


const HeaderStats = () => {
    const [countryInfo, setCountryInfo] = useState({});

    useEffect(() => {
        fetch("https://disease.sh/v3/covid-19/all")
          .then((response) => response.json())
          .then((data) => {
            setCountryInfo(data);
          });
    }, []);

    const mildCases =  parseInt(countryInfo.active)-parseInt(countryInfo.critical);

    return(
        <Row>
            <Col lg={3} sm={12}>
              <MainStatsCard
                title= 'ACTIVE CASES'
                cases1 = {countryInfo.active}
                cases2 = {countryInfo.critical}
                cases3 = {mildCases}
              />
            </Col>
            <Col lg={3} sm={12}>
              <MainStatsCard
                activeCases = {countryInfo.active}
              />
            </Col>
            <Col lg={3} sm={12}>
              <MainStatsCard
                activeCases = {countryInfo.active}
              />
            </Col>
            <Col lg={3} sm={12}>
              <MainStatsCard
                activeCases = {countryInfo.active}
              />
            </Col>
          </Row>
    );


};

export default HeaderStats
