import React from "react";
import ReactDOM from "react-dom";
import StickyHeadTable from "./Table2";

const columns = [
    { id: 'country', label: 'Country', minWidth: 170 },
    { id: 'cases', label: 'Cases', minWidth: 100 },
  ];
  
  
  function createData(country, cases, deaths, todayDeaths, recovered, todayRecovered, active, critical) {
    return { country, cases, deaths, todayDeaths, recovered, todayRecovered, active, critical };
  }

const TableContainer =  () => {

  const [rows, setRows] = React.useState([])

  useEffect(() => {
    const getCountriesData = async () => {
      fetch("https://disease.sh/v3/covid-19/countries")
        .then((response) => response.json())
        .then((data) => {
           const rows = data.map((country) => (
             createData(country.country, country.cases)
            
          ))
          console.log(rows);
                    
        });
    };

    getCountriesData();
  }, []);

  console.log(data);

  return (
    <div className="App">
      <StickyHeadTable columns={columns} rows={rows} />
    </div>
  );
}

export default TableContainer;
